package main

import (
	"log"
	"net/http"

	plugindserver "github.com/ryanwclark/accent-voice/src"
)

func main() {
	log.Printf("Server started")

	ConfigAPIService := plugindserver.NewConfigAPIService()
	ConfigAPIController := plugindserver.NewConfigAPIController(ConfigAPIService)

	MarketAPIService := plugindserver.NewMarketAPIService()
	MarketAPIController := plugindserver.NewMarketAPIController(MarketAPIService)

	PluginAPIService := plugindserver.NewPluginAPIService()
	PluginAPIController := plugindserver.NewPluginAPIController(PluginAPIService)

	StatusAPIService := plugindserver.NewStatusAPIService()
	StatusAPIController := plugindserver.NewStatusAPIController(StatusAPIService)

	router := plugindserver.NewRouter(ConfigAPIController, MarketAPIController, PluginAPIController, StatusAPIController)

	log.Fatal(http.ListenAndServe(":8080", router))
}
